import React from "react";
import AppHeader from "../AppHeader";
import PostAddForm from "../PosrAddForm";
import PostList from "../PostList/PostList";
import PostStatusFilter from "../PostStastusFilter";
import SearchPanel from "../SearchPanel";
import './App.css'

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [
                { label: "Going to React JS", important: true, id: 'qw' },
                { label: "That is so good", important: false, id: 'qd' },
                { label: "I need a break... ", important: false, id: 'qs' }

            ]
        };
        this.deleteItem=this.deleteItem.bind(this)
    }
    deleteItem (id) {
        this.setState(({data}) => {
            const index =data.findIndex(elem => elem.id ===id);

            const before =data.slice(0,index)
            const after =data.slice(index+1)

            const newArry =[...before,...after]
        })
    }


    render() {
        return (
            <div className="app">
                <AppHeader />
                <div className="search-panel d-flex">
                    <SearchPanel />
                    <PostStatusFilter />
                </div>
                <PostList posts={this.state.data} onDelete={this.deleteItem} />
                <PostAddForm />
            </div>

        )
    }
}
